import React, { memo, useEffect, useState } from "react";

import DynamicIcon from "./DynamicIcon";
import useNotifications from "../hooks/useNotifications";

const stayOnScreen = 15 * 1000;

function ToastNotification() {
	const notifications = useNotifications();
	const [showNotification, setShowNotification] = useState(true);

	const onClose = useCallback(() => {}, [notifications]);

	useEffect(() => {
		if (notifications) {
			setShowNotification(false);
		}

		const to = setTimeout(() => {
			setShowNotification(false);
		}, stayOnScreen);

		return () => {
			clearTimeout(to);
		};
	}, [notifications]);

	return (
		notifications && (
			<div
				className="toast-notification"
				style={
					notifications && showNotification == true
						? { opacity: 1 }
						: { opacity: 0 }
				}
			>
				<div
					className="toast-notification-close"
					onClick={() => setShowNotification(false)}
				>
					<DynamicIcon name="CiCircleRemove" size={20} />
				</div>
				{notifications}
			</div>
		)
	);
}

export default memo(ToastNotification);
